import React from "react";
import { useEffect, useState } from "react";
import { vendiaClient } from "./vendiaClient";

const { client }  = vendiaClient();

export const Demo = () => {

    const[device, setDevice] = useState();
    const[testName, setTestName] = useState();
    const[testList, setTestList] = useState();

    useEffect(() => {
        const listTest = async () => {
            const listTestResponse = await client.entities.test.list();
            console.log(listTestResponse?.items);
            setTestList(listTestResponse?.items);
        }
        //listTest();
    }, [])

    const addDevice = async () => {
        const addDeviceResponse = await client.entities.test.add({
            Device: device,
            TestName: testName
        })
        console.log(addDeviceResponse);
    }

    const handleDeviceChange = (event) => {
        setDevice(event.target.value);
    }

    const handleTestNameChange = (event) => {
        setTestName(event.target.value);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        addDevice();
    }

    // <button onClick={() => {addDevice()}}>Add Device</button>
    return (
        <div className="dev-mode-plugin">
        <div className="div">
            <div className="content">

            <div className="text">
                <div className="text-wrapper">
                Dev Mode
                <br />
                Plugin
                </div>
            </div>
            </div>
            <div className="image">
            <div className="anima-inspect-plugin">
                <img className="img" alt="Image" src="image-4.png" />
                <img className="image-2" alt="Image" src="image-6.png" />
                <img className="image-3" alt="Image" src="image-5.png" />
            </div>
            <img className="usercard-in-dev-mode" alt="Usercard in dev mode" src="usercard-in-dev-mode.png" />
            </div>
        </div>
        </div>



