import React from "react";
import { useEffect, useState } from "react";
import { vendiaClient } from "./VendiaClient";
import { DataGrid } from "@mui/x-data-grid";

const { client } = vendiaClient();


export const DevicePage = () => {

    const[testList, setTestList] = useState();

    useEffect(() => {
        const filterTests = async () => {
            const filteredTestList = await client.entities.test.list({
                filter: {
                  Device: {
                    contains: 'Device1',
                  }
                },
              })
            /*console.log(filteredTestList?.items);*/
            setTestList(filteredTestList?.items);

        }
        filterTests();
    }, [])

    const boolToString = (value) =>
    {
        if(typeof(value) === 'boolean')
            return 'true';
        else
            return 'false'
    }

    const columns = [
        {field: 'id', headerName: 'TestID', width: 90},
        {field: 'OrgAssignment', headerName: 'OrgAssignment', width: 150},
        {field: 'TestName', headerName: 'TestName', width: 90},
        {field: 'TestMethod', headerName: 'TestMethod', width: 90},
        {field: 'Notes', headerName: 'Notes', width: 90},
        {field: 'Completed', headerName: 'Completed', width: 90},
        {field: 'UpdatedBy', headerName: 'UpdatedBy', width: 90},
    ];

    
    
    const rows = testList?.map((test) => ({
        id: test?.TestID,
        OrgAssignment: test?.OrgAssignment,
        TestName: test?.TestName,
        TestMethod: test?.TestMethod,
        Notes: test?.Notes,
        Completed: boolToString(test?.Completed),
        UpdatedBy: test?.UpdatedBy
        
    }));
    /*console.log(testList);*/

    return(
        
        <div className="DevicePage">
            <h1> Device1 </h1>
            <div>
            <DataGrid 
                    rows={rows} 
                    columns={columns}  
                />
            </div>
            
            
        </div>

    )
}
